import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({
        orgName: '',
        firstName: '',
        lastName: '',
        email: '',
        state: '',
        password: '',
        userType: 'dev' 
    });
    const [isEditing, setIsEditing] = useState(false);
    const [currentUserId, setCurrentUserId] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8000/admin/users')
            .then(response => {
                console.log(response.data);  
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Erreur de récupération des utilisateurs:', error);
                alert('Erreur de récupération des utilisateurs');
            });
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewUser({ ...newUser, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const { userType, ...dataToSend } = newUser; 

        if (userType === 'dev') {
            delete dataToSend.orgName; 
        }

        if (isEditing) {
    
            axios.put(`http://localhost:8000/admin/users/${currentUserId}`, newUser)
                .then(response => {
                    alert(response.data.message);
                    setUsers(prevUsers => prevUsers.map(user => 
                        user._id === currentUserId ? { ...user, ...newUser } : user
                    ));
                    setIsEditing(false);
                    setNewUser({
                        orgName: '',
                        firstName: '',
                        lastName: '',
                        email: '',
                        state: '',
                        password: ''
                    });
                    setCurrentUserId(null);
                })
                .catch(error => console.error(error));
        } else {
           
            axios.post('http://localhost:8000/admin/users', newUser)
                .then(response => {
                    alert(response.data.message);
                    setUsers(prevUsers => [...prevUsers, response.data.user]);
                    setNewUser({
                        orgName: '',
                        firstName: '',
                        lastName: '',
                        email: '',
                        state: '',
                        password: ''
                    });
                })
                .catch(error => console.error(error));
        }
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:8000/admin/users/${id}`)
            .then(response => {
                alert(response.data.message);
                setUsers(prevUsers => prevUsers.filter(user => user._id !== id));  
            })
            .catch(error => console.error(error));
    };

    const handleEdit = (user) => {
        setNewUser({
            orgName: user.orgName,
            firstName: user.firstName,
            lastName: user.lastName,
            email: user.email,
            state: user.state,
            password: ''  
        });
        setIsEditing(true);
        setCurrentUserId(user._id);
    };

    return (
        <div className="admin-dashboard-container">
    
    <form onSubmit={handleSubmit} className="admin-form">
    <select
        name="userType"
        value={newUser.userType}
        onChange={handleChange}
        className="admin-input"
    >
        <option value="dev">Developer</option>
        <option value="org">Organization</option>
    </select>
    {newUser.userType === 'org' && (
        <input
            type="text"
            name="orgName"
            placeholder="Organization Name"
            value={newUser.orgName}
            onChange={handleChange}
            className="admin-input"
            required
        />
    )}
    <input
        type="text"
        name="firstName"
        placeholder="First Name"
        value={newUser.firstName}
        onChange={handleChange}
        className="admin-input"
    />
    <input
        type="text"
        name="lastName"
        placeholder="Last Name"
        value={newUser.lastName}
        onChange={handleChange}
        className="admin-input"
    />
    <input
        type="email"
        name="email"
        placeholder="Email"
        value={newUser.email}
        onChange={handleChange}
        className="admin-input"
    />
    <input
        type="text"
        name="state"
        placeholder="State"
        value={newUser.state}
        onChange={handleChange}
        className="admin-input"
    />
    <input
        type="password"
        name="password"
        placeholder="Password"
        value={newUser.password}
        onChange={handleChange}
        className="admin-input"
    />
    <button type="submit" className="admin-submit-btn">
        {isEditing ? 'Update User' : 'Add User'}
    </button>
</form>

    <h3 className="admin-users-title">All Users</h3>
                <ul className="admin-users-list">
        {users.map(user => (
        <li key={user._id} className="admin-user-item">
        <div className="admin-user-info">
        {user.email}
        </div>
        <div className="admin-buttons">
            <button onClick={() => handleEdit(user)} className="admin-edit-btn">Modifier</button>
            <button onClick={() => handleDelete(user._id)} className="admin-delete-btn">Supprimer</button>
        </div>
        </li>
    ))}
    </ul>
            </div>
    );
};

export default AdminDashboard;
