import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {


    return (
        <div className="admin-dashboard-container">
            <form onSubmit={handleSubmit} className="admin-form">
                <select
                    name="userType"
                    value={newUser.userType}
                    onChange={handleChange}
                    className="admin-input"
                >
                    <option value="dev">Developer</option>
                    <option value="org">Organization</option>
                </select>
                {newUser.userType === 'org' && (
                    <input
                        type="text"
                        name="orgName"
                        placeholder="Organization Name"
                        value={newUser.orgName}
                        onChange={handleChange}
                        className="admin-input"
                        required
                    />
                )}
                <input
                    type="text"
                    name="firstName"
                    placeholder="First Name"
                    value={newUser.firstName}
                    onChange={handleChange}
                    className="admin-input"
                />
                <input
                    type="text"
                    name="lastName"
                    placeholder="Last Name"
                    value={newUser.lastName}
                    onChange={handleChange}
                    className="admin-input"
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newUser.email}
                    onChange={handleChange}
                    className="admin-input"
                />
                <input
                    type="text"
                    name="state"
                    placeholder="State"
                    value={newUser.state}
                    onChange={handleChange}
                    className="admin-input"
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={newUser.password}
                    onChange={handleChange}
                    className="admin-input"
                />
                <button type="submit" className="admin-submit-btn">
                    {isEditing ? 'Update User' : 'Add User'}
                </button>
            </form>

            <h3 className="admin-users-title">All Users</h3>
            <ul className="admin-users-list">
                {users.length > 0 ? (
                    users.map(user => (
                        user && user.email ? (
                            <li key={user._id} className="admin-user-item">
                                <div className="admin-user-info">
                                    {user.email}
                                </div>
                                <div className="admin-buttons">
                                    <button onClick={() => handleEdit(user)} className="admin-edit-btn">Modifier</button>
                                    <button onClick={() => handleDelete(user._id)} className="admin-delete-btn">Supprimer</button>
                                </div>
                            </li>
                        ) : null
                    ))
                ) : (
                    <p>Aucun utilisateur trouvé.</p>
                )}
            </ul>
        </div>
    );
};

export default AdminDashboard;
