import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('dev');  
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8000/api/users/login', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, userType }),
      });

      if (response.ok) {
        const data = await response.json();
        alert('Connexion réussie !');
        navigate('/dashboard'); 
      } else {
        alert('Erreur de connexion');
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert('Erreur serveur');
    }
  };

  return (
    <div className="login-container">
      <h2 className="login-title">Welcome back!</h2>
      <h2 className="login-subtitle">
        {userType === 'dev' ? "Let's connect you to a job!" : "Let's find you some candidates!"}
      </h2>
      <div className="user-type-selector">
        <label>
          <input 
            type="radio" 
            value="dev" 
            checked={userType === 'dev'} 
            onChange={() => setUserType('dev')} 
          />
          Developer
        </label>
        <label>
          <input 
            type="radio" 
            value="org" 
            checked={userType === 'org'} 
            onChange={() => setUserType('org')} 
          />
          Organization
        </label>
      </div>

      <form className="login-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email" className="login-label"><h3>Email :</h3></label>
          <input
            type="email"
            id="email"
            placeholder="Email"
            className="login-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password" className="login-label"><h3>Password :</h3></label>
          <input
            type="password"
            id="password"
            placeholder="Password"
            className="login-input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="button-container">
          <button type="submit" className="login-button">Log In</button>
        </div>
      </form>

      {userType === 'dev' ? (
        <p>
          <a href="/login-organization" onClick={() => navigate('/login-organization')}>
            Need to login as an organization?
          </a>
        </p>
      ) : (
        <p>
          <a href="/login-developer" onClick={() => navigate('/login-developer')}>
            Need to login as a developer?
          </a>
        </p>
      )}

      <p>
        <a href="/signup-developer" onClick={() => navigate('/signup-developer')}>
          Don't have an account? Sign Up
        </a>
      </p>
    </div>
  );
};

export default Login;
