import React, { useState } from 'react';
import AddLanguages from './AddLanguages';
import AddFrameworks from './AddFrameworks';
import './DevDashboard.css';


const DevDashboard = () => {
    const [step, setStep] = useState(1);
    const [isRegistrationSuccessful, setIsRegistrationSuccessful] = useState(false);

    const handleNextStep = () => setStep(2);
    const handleCompleteProfile = () => {
        setIsRegistrationSuccessful(true);
    };

    return (
        <div className="container">
            {isRegistrationSuccessful && (
                <div className="success-message" style={{ color: 'green', marginBottom: '20px' }}>
                    Registration successful
                </div>
            )}
            {step === 1 && <AddLanguages onNext={handleNextStep} />}
            {step === 2 && <AddFrameworks onComplete={handleCompleteProfile} />}
        </div>
    );
};

export default DevDashboard;
