import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({
        orgName: '',
        firstName: '',
        lastName: '',
        email: '',
        state: '',
        password: ''
    });

    useEffect(() => {
      axios.get('/admin/users')
          .then(response => {
              console.log(response.data); 
              setUsers(response.data);
          })
          .catch(error => {
              console.error('Erreur de récupération des utilisateurs:', error);
              alert('Erreur de récupération des utilisateurs');
          });
  }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('/admin/users', newUser)
        .then(response => {
            alert(response.data.message);
            setUsers(prevUsers => [...prevUsers, newUser]);
            setNewUser({
                orgName: '',
                firstName: '',
                lastName: '',
                email: '',
                state: '',
                password: ''
            });
        })
        .catch(error => console.error(error));
    };

    const handleDelete = (id) => {
        axios.delete(`/admin/users/${id}`)
            .then(response => {
                alert(response.data.message);
                setUsers(users.filter(user => user._id !== id));  
            })
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h2>Tableau de bord Admin</h2>

            <form onSubmit={handleSubmit}>
                <input type="text" name="orgName" placeholder="Organisation" value={newUser.orgName} onChange={handleChange} />
                <input type="text" name="firstName" placeholder="Prénom" value={newUser.firstName} onChange={handleChange} />
                <input type="text" name="lastName" placeholder="Nom" value={newUser.lastName} onChange={handleChange} />
                <input type="email" name="email" placeholder="Email" value={newUser.email} onChange={handleChange} />
                <input type="text" name="state" placeholder="État" value={newUser.state} onChange={handleChange} />
                <input type="password" name="password" placeholder="Mot de passe" value={newUser.password} onChange={handleChange} />
                <button type="submit">Ajouter un utilisateur</button>
            </form>

            <h3>Utilisateurs</h3>
            <ul>
                {users.map(user => (
                    <li key={user._id}>
                        {user.firstName} {user.lastName} ({user.email})
                        <button onClick={() => handleDelete(user._id)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
