import React, { useState } from 'react';
import { SiJavascript, SiPython, SiJava, SiCplusplus, SiRuby, SiPhp, SiSwift, SiGo, SiTypescript } from 'react-icons/si';

const AddLanguages = ({ onNext }) => {
    const [selectedLanguages, setSelectedLanguages] = useState([]);
    
    const languages = [
        { name: 'JavaScript', icon: <SiJavascript /> },
        { name: 'Python', icon: <SiPython /> },
        { name: 'Java', icon: <SiJava /> },
        { name: 'C++', icon: <SiCplusplus /> },
        { name: 'Ruby', icon: <SiRuby /> },
        { name: 'PHP', icon: <SiPhp /> },
        { name: 'Swift', icon: <SiSwift /> },
        { name: 'Go', icon: <SiGo /> },
        { name: 'TypeScript', icon: <SiTypescript /> }
    ];

    const toggleLanguage = (language) => {
        setSelectedLanguages((prev) =>
            prev.includes(language)
                ? prev.filter((lang) => lang !== language)
                : [...prev, language]
        );
    };

    return (
        <div>
            <h2>Add Your Skills</h2>
            <h3>Pick Your Top 5 Languages</h3>
            <div className="languages-list">
                {languages.map((language) => (
                    <button
                        key={language.name}
                        onClick={() => toggleLanguage(language.name)}
                        className={selectedLanguages.includes(language.name) ? 'selected' : ''}
                    >
                        {language.icon} {language.name}
                    </button>
                ))}
            </div>
            <textarea
                placeholder="Short Bio"
                rows="4"
                cols="50"
                style={{ marginTop: '10px' }}
            />
            <button onClick={onNext}>Next step: Frameworks and Libraries</button>
        </div>
    );
};

export default AddLanguages;
