import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Charger la liste des utilisateurs
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token');  // Assurez-vous que le token est dans le localStorage
        const response = await axios.get('/api/admin/users', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(response.data);
      } catch (err) {
        setError('Failed to fetch users');
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Supprimer un utilisateur
  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(user => user._id !== id));
    } catch (err) {
      setError('Failed to delete user');
    }
  };

  // Mettre à jour un utilisateur
  const handleUpdate = async (id, updatedData) => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(`/api/admin/users/${id}`, updatedData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.map(user => (user._id === id ? response.data : user)));
    } catch (err) {
      setError('Failed to update user');
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      {error && <p>{error}</p>}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2>User List</h2>
          <ul>
            {users.map(user => (
              <li key={user._id}>
                <span>{user.firstName} {user.lastName} ({user.email})</span>
                <button onClick={() => handleUpdate(user._id, { role: 'user' })}>Update</button>
                <button onClick={() => handleUpdate(user._id, { role: 'admin' })}>Update</button>
                <button onClick={() => handleDelete(user._id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
