import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editUserData, setEditUserData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userRole = localStorage.getItem('userRole');
  
    if (userRole !== 'admin') {
      setError('Access denied. Admins only.');
      return;
    }
  
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/admin/users', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(response.data);
      } catch (err) {
        setError('Error fetching users');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
  
    fetchUsers();
  }, []);
  
  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(user => user._id !== id));
    } catch (err) {
      setError('Failed to delete user');
    }
  };

  const handleEdit = (user) => {
    setIsEditing(true);
    setEditUserData({ ...user });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditUserData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(`/api/admin/users/${editUserData._id}`, editUserData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.map(user => (user._id === response.data._id ? response.data : user)));
      setIsEditing(false);
      setEditUserData(null);
    } catch (err) {
      setError('Failed to update user');
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      {error && <p>{error}</p>}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2>User List</h2>
          <ul>
            {users.map(user => (
              <li key={user._id}>
                <span>{user.firstName} {user.lastName} ({user.email})</span>
                <button onClick={() => handleEdit(user)}>Update</button>
                <button onClick={() => handleDelete(user._id)}>Delete</button>
              </li>
            ))}
          </ul>

          {isEditing && (
            <div>
              <h3>Update User</h3>
              <form onSubmit={handleUpdate}>
                <div>
                  <label>First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={editUserData.firstName}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={editUserData.lastName}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label>Email</label>
                  <input
                    type="email"
                    name="email"
                    value={editUserData.email}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label>State</label>
                  <input
                    type="text"
                    name="state"
                    value={editUserData.state}
                    onChange={handleChange}
                  />
                </div>

                <button type="submit">Update User</button>
              </form>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
