import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

const SignUp = () => {
  const navigate = useNavigate();
  const [userType, setUserType] = useState('dev');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    state: '',
    password: '',
    confirmPassword: '',  
    orgName: '',
  });
  const [errors, setErrors] = useState({});

  const states = ["Bizerte", "Tunis", "Sousse", "Nabeul", "Sfax"];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting form data:", formData);
  
    const { confirmPassword, ...dataToSend } = formData; // Exclude confirmPassword from the request
  
    if (userType === 'org' && !dataToSend.orgName) {
      setErrors({ orgName: 'Le nom de l\'organisation est requis.' });
      return;
    }
  
    const requestBody = {
      ...dataToSend,
      userType: userType === 'dev' ? undefined : 'org', // Set userType to "org" only if it's an organisation
      orgName: userType === 'dev' ? undefined : dataToSend.orgName, // Exclude orgName for developers
    };
  
    try {
      const response = await fetch('http://localhost:8000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });
  
      const data = await response.json();
      console.log('Server Response:', data);
  
      if (response.ok) {
        console.log("User registered:", data);
        alert('Utilisateur enregistré avec succès !');
        navigate('/');
      } else {
        console.error("Registration error:", data);
        const formErrors = data.errors ? data.errors.reduce((acc, error) => {
          acc[error.field] = error.message;
          return acc;
        }, {}) : {}; // Ensure errors exist before reducing
        setErrors(formErrors);
      }
    } catch (error) {
      console.error("Fetch error:", error);
      setErrors({ general: 'Une erreur est survenue. Veuillez réessayer.' });
    }
  };
  
  
  
  return (
    <div className="container">
      <div className="form">
        <form onSubmit={handleSubmit}>
          {errors.general && <p className="error-message">{errors.general}</p>}

          {userType === 'org' && (
            <div className="form-group">
              <label className="label">Org Name :</label>
              <input
                type="text"
                name="orgName"
                value={formData.orgName}
                onChange={handleChange}
                className="input"
                placeholder="Enter your organization name"
                required
              />
              {errors.orgName && <p className="error-message">{errors.orgName}</p>}
            </div>
          )}
          <div className="form-group">
            <label className="label">First Name :</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              className="input"
              placeholder="Enter your first name"
              required
            />
            {errors.firstName && <p className="error-message">{errors.firstName}</p>}
          </div>
          <div className="form-group">
            <label className="label">Last Name :</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              className="input"
              placeholder="Enter your last name"
              required
            />
            {errors.lastName && <p className="error-message">{errors.lastName}</p>}
          </div>
          <div className="form-group">
            <label className="label">Email :</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="input"
              placeholder="Enter your email"
              required
            />
            {errors.email && <p className="error-message">{errors.email}</p>}
          </div>
          <div className="form-group">
            <label className="label">State :</label>
            <select
              name="state"
              value={formData.state}
              onChange={handleChange}
              className="input"
              required
            >
              <option value="">Select a state</option>
              {states.map((state) => (
                <option key={state} value={state}>{state}</option>
              ))}
            </select>
            {errors.state && <p className="error-message">{errors.state}</p>}
          </div>
          <div className="form-group">
            <label className="label">Password :</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="input"
              placeholder="Enter your password"
              required
            />
            {errors.password && <p className="error-message">{errors.password}</p>}
          </div>
          <div className="form-group">
            <label className="label">Confirm Password :</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              className="input"
              placeholder="Confirm your password"
            />
            {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>}
          </div>
          <div className="button-container">
            <button type="submit" className="button">Register</button>
          </div>
        </form>

        <p>
          <a href="#" onClick={(e) => {
            e.preventDefault();
            setUserType(userType === 'dev' ? 'org' : 'dev');
          }}
            className="link-toggle" style={{ cursor: 'pointer' }}>
            {userType === 'dev' ? "Need to sign up as an organization?" : "Need to sign up as a developer?"}
          </a>
        </p>
      </div>
    </div>
  );
};

export default SignUp;
