import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './signup.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  // Récupérer tous les utilisateurs
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/api/admin/users'); // L'URL de l'API peut changer en fonction de votre configuration
        setUsers(response.data);
        setIsLoading(false);
      } catch (err) {
        setError('Error fetching users');
        setIsLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Supprimer un utilisateur
  const handleDelete = async (userId) => {
    try {
      await axios.delete(`/api/admin/users/${userId}`);
      setUsers(users.filter(user => user._id !== userId)); // Met à jour la liste des utilisateurs après suppression
    } catch (err) {
      setError('Error deleting user');
    }
  };

  // Mettre à jour un utilisateur (vous pouvez ajouter un formulaire pour cela)
  const handleUpdate = async (userId, updatedData) => {
    try {
      const response = await axios.put(`/api/admin/users/${userId}`, updatedData);
      const updatedUser = response.data;
      setUsers(users.map(user => (user._id === updatedUser._id ? updatedUser : user)));
    } catch (err) {
      setError('Error updating user');
    }
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <div className="user-list">
        <h2>Users</h2>
        <table>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>State</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.firstName}</td>
                <td>{user.lastName}</td>
                <td>{user.email}</td>
                <td>{user.state}</td>
                <td>
                  <button onClick={() => handleUpdate(user._id, { firstName: 'New FirstName', lastName: 'New LastName', state: 'New State' })}>
                    Update
                  </button>
                  <button onClick={() => handleDelete(user._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="links">
        <Link to="/admin/create-user">Create New User</Link>
      </div>
    </div>
  );
};

export default AdminDashboard;
