import React, { useState } from 'react';
import './Login.css';

const OrgLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/OrgUser/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, userType: 'org' }),
      });

      if (response.ok) {
        // Gérer la réponse de connexion réussie
        alert('Connexion réussie pour l\'organisation !');
      } else {
        // Gérer les erreurs de connexion
        alert('Erreur de connexion');
      }
    } catch (error) {
      console.error('Erreur:', error);
    }
  };

  return (
    <div className="login-container">
      <h2 className="login-title">Welcome back!</h2>
      <h2 className="login-subtitle">Let's find you some candidates!</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email" className="login-label"><h3>Email :</h3></label>
          <input
            type="email"
            id="email"
            placeholder="Email"
            className="login-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password" className="login-label"><h3>Password :</h3></label>
          <input
            type="password"
            id="password"
            placeholder="Password"
            className="login-input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="button-container">
          <button type="submit" className="login-button">Log In</button>
        </div>
      </form>
    </div>
  );
}

export default OrgLogin;
