import React, { useState } from 'react';
import { SiReact, SiVuedotjs, SiAngular, SiDjango, SiRubyonrails, SiSpring, SiLaravel, SiNodedotjs, SiFlutter } from 'react-icons/si';


const AddFrameworks = ({ onComplete }) => {
    const [selectedFrameworks, setSelectedFrameworks] = useState([]);
    
    const frameworks = [
        { name: 'React', icon: <SiReact /> },
        { name: 'Vue', icon: <SiVuedotjs /> },
        { name: 'Angular', icon: <SiAngular /> },
        { name: 'Django', icon: <SiDjango /> },
        { name: 'Rails', icon: <SiRubyOnRails /> },
        { name: 'Spring', icon: <SiSpring /> },
        { name: 'Laravel', icon: <SiLaravel /> },
        { name: 'Node.js', icon: <SiNodedotjs /> },
        { name: 'Flutter', icon: <SiFlutter /> }
    ];

    const toggleFramework = (framework) => {
        setSelectedFrameworks((prev) =>
            prev.includes(framework)
                ? prev.filter((fw) => fw !== framework)
                : [...prev, framework]
        );
    };

    return (
        <div>
            <h2>Add Your Skills</h2>
            <h3>Pick Your Top 5 Frameworks or Libraries</h3>
            <div className="frameworks-list">
                {frameworks.map((framework) => (
                    <button
                        key={framework.name}
                        onClick={() => toggleFramework(framework.name)}
                        className={selectedFrameworks.includes(framework.name) ? 'selected' : ''}
                    >
                        {framework.icon} {framework.name}
                    </button>
                ))}
            </div>
            <button onClick={onComplete}>Complete Profile</button>
        </div>
    );
};

export default AddFrameworks;
