import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({
        orgName: '',
        firstName: '',
        lastName: '',
        email: '',
        state: '',
        password: ''
    });
    const [editingUser, setEditingUser] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:5000/admin/users')
            .then(response => {
                console.log(response.data);  
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Erreur de récupération des utilisateurs:', error);
                alert('Erreur de récupération des utilisateurs');
            });
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewUser({ ...newUser, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (editingUser) {
            axios.put(`http://localhost:5000/admin/users/${editingUser._id}`, newUser)
                .then(response => {
                    alert(response.data.message);
                    setUsers(prevUsers => prevUsers.map(user => user._id === editingUser._id ? newUser : user));
                    setEditingUser(null);
                    setNewUser({
                        orgName: '',
                        firstName: '',
                        lastName: '',
                        email: '',
                        state: '',
                        password: ''
                    });
                })
                .catch(error => console.error(error));
        } else {
            axios.post('http://localhost:5000/admin/users', newUser)
                .then(response => {
                    alert(response.data.message);
                    setUsers(prevUsers => [...prevUsers, newUser]);  
                    setNewUser({
                        orgName: '',
                        firstName: '',
                        lastName: '',
                        email: '',
                        state: '',
                        password: ''
                    });
                })
                .catch(error => console.error(error));
        }
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:5000/admin/users/${id}`)
            .then(response => {
                alert(response.data.message);
                setUsers(prevUsers => prevUsers.filter(user => user._id !== id));  
            })
            .catch(error => console.error(error));
    };

    const handleEdit = (user) => {
        setEditingUser(user);
        setNewUser({
            orgName: user.orgName,
            firstName: user.firstName,
            lastName: user.lastName,
            email: user.email,
            state: user.state,
            password: ''  // Don't prefill password
        });
    };

    return (
        <div className="admin-dashboard">
            <h2>Tableau de bord Admin</h2>

            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="orgName"
                    placeholder="Organisation"
                    value={newUser.orgName}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="firstName"
                    placeholder="Prénom"
                    value={newUser.firstName}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="lastName"
                    placeholder="Nom"
                    value={newUser.lastName}
                    onChange={handleChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newUser.email}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="state"
                    placeholder="État"
                    value={newUser.state}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Mot de passe"
                    value={newUser.password}
                    onChange={handleChange}
                />
                <button type="submit">{editingUser ? 'Mettre à jour' : 'Ajouter'} un utilisateur</button>
            </form>

            <h3>Utilisateurs</h3>
            <ul>
                {users.map(user => (
                    <li key={user._id}>
                        {user.firstName} {user.lastName} ({user.email})
                        <button onClick={() => handleEdit(user)}>Modifier</button>
                        <button onClick={() => handleDelete(user._id)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
