import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

const SignUp = () => {
  const navigate = useNavigate();
  const [userType, setUserType] = useState('dev'); 
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    address: '',
    state: '',
    password: '',
    confirmPassword: '',
    orgName: '',
  });

  const states = ["Bizerte", "Tunis", "Sousse", "Nabeul", "Sfax"];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting form data:", formData);

    const requestBody = {
        ...formData,
        userType: userType, 
    };
    if (userType === 'dev') {
        delete requestBody.orgName;
    }

    try {
        const response = await fetch('http://localhost:8000/api/users/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody),
        });

        const data = await response.json();
        if (response.ok) {
            console.log("User registered:", data);
            alert('Utilisateur enregistré avec succès !');
            navigate('/'); 
        } else {
            console.error("Registration error:", data);
        }
    } catch (error) {
        console.error("Fetch error:", error);
    }
};

  return (
    <div className="signup-container">
      <h2 className="signup-title">{userType === 'dev' ? "Developer Sign Up" : "Organization Sign Up"}</h2>
      <div className="signup-form">
        <form onSubmit={handleSubmit}>
          {userType === 'org' && (
            <div className="form-group">
              <label className="signup-label">Org Name :</label>
              <input
                type="text"
                name="orgName"
                value={formData.orgName}
                onChange={handleChange}
                className="signup-input"
                placeholder="Enter your organization name"
                required
              />
            </div>
          )}
          <div className="form-group">
            <label className="signup-label">First Name :</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              className="signup-input"
              placeholder="Enter your first name"
              required
            />
          </div>
          <div className="form-group">
            <label className="signup-label">Last Name :</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              className="signup-input"
              placeholder="Enter your last name"
              required
            />
          </div>
          <div className="form-group">
            <label className="signup-label">Email :</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="signup-input"
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="form-group">
            <label className="signup-label">Address :</label>
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              className="signup-input"
              placeholder="Enter your address"
              required
            />
          </div>
          <div className="form-group">
            <label className="signup-label">State :</label>
            <select
              name="state"
              value={formData.state}
              onChange={handleChange}
              className="signup-input"
              required
            >
              <option value="">Select a state</option>
              {states.map((state) => (
                <option key={state} value={state}>{state}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label className="signup-label">Password :</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="signup-input"
              placeholder="Enter your password"
              required
            />
          </div>
          <div className="form-group">
            <label className="signup-label">Confirm Password :</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              className="signup-input"
              placeholder="Confirm your password"
              required
            />
          </div>
          <div className="button-container">
            <button type="submit" className="signup-button">Register</button>
          </div>
        </form>

        <p>
          <a href="#" onClick={(e) => {
            e.preventDefault();
            setUserType(userType === 'dev' ? 'org' : 'dev');
          }}
            className="link-toggle" style={{ cursor: 'pointer' }}>
            {userType === 'dev' ? "Need to sign up as an organization?" : "Need to sign up as a developer?"}
          </a>
        </p>
      </div>
    </div>
  );
};

export default SignUp;
