import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Remplacer useHistory par useNavigate

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();  // Utilisation de useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Logique de validation et soumission
    try {
      // Exemple de soumission avec axios ou fetch
      // Si la connexion réussit, rediriger
      navigate('/dashboard');  // Redirige vers /dashboard après une connexion réussie
    } catch (err) {
      setError('Erreur de connexion');
    }
  };

  return (
    <div className="login-container">
  <h2>Login</h2>
  <form onSubmit={handleSubmit} className="login-form">
    {error && <p className="error-message">{error}</p>}
    <label htmlFor="email">Email</label>
    <input
      type="email"
      id="email"
      name="email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
    />
    <label htmlFor="password">Password</label>
    <input
      type="password"
      id="password"
      name="password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    <button type="submit">Login</button>
  </form>
  <div className="signup-link">
    <p>Don't have an account? <a href="/signup">Sign up</a></p>
  </div>
</div>

  );
};

export default Login;
