import React, { useState, useEffect } from 'react';
import './Home.css';

const HomePage = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false); // Simuler le chargement de la page
    }, 2000); // Attendre 2 secondes avant de charger le contenu
  }, []);

  return (
    <div className="homepage">

        <>
          <header className="homepage-header">
            <h2 className="title">Devs On Deck</h2>
            <p className="subtitle">
              Your platform for connecting developers and job opportunities.
            </p>
            <div className="search-bar">
              <input type="text" placeholder="Search for jobs or companies..." />
              <button>Search</button>
            </div>
          </header>

          <div className="sections">
            <div className="section">
              <h3>About Us</h3>
              <p>Learn more about the platform and its features.</p>
            </div>
            <div className="section">
              <h3>Our Mission</h3>
              <p>We aim to connect talented developers with top job opportunities.</p>
            </div>
            <div className="section">
              <h3>FAQ</h3>
              <p>Find answers to frequently asked questions.</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default HomePage;
